mapper.update:
UPDATE 
	place
SET 
	place_nm = #{place_nm}
	, place_addr = #{place_addr}
	, place_tel = #{place_tel}
	, place_content = #{place_content}
	, place_in = #{place_in}
	, place_out = #{place_out}
	, loc_id = CASE
		WHEN #{loc_nm} = '서울' THEN 1
		WHEN #{loc_nm} = '경기도' THEN 2
		WHEN #{loc_nm} = '강원도' THEN 3
		WHEN #{loc_nm} = '경상도' THEN 4
		WHEN #{loc_nm} = '전라도' THEN 5
		WHEN #{loc_nm} = '충청도' THEN 6
		WHEN #{loc_nm} = '제주도' THEN 7
		ELSE 8 END
WHERE 
	place_id = #{place_id}

update.foreach:
INSERT INTO
	 place_picture (picture_source, picture_origin_name, status, place_id)
SELECT
	 #{pic}, 'static_origin_name', 'active', #{place_id}
FROM DUAL
WHERE NOT EXISTS (
    SELECT 1 FROM place_picture WHERE picture_source = #{pic} AND place_id = #{place_id}
);

mapper.insert:
INSERT INTO place_picture (
picture_source,
place_id,
status
)
VALUES(#{picture_source}, #{place_id}, #{status});

mapper.update:
UPDATE place
SET status = 'inactive'
WHERE place_id = #{place_id}

UPDATE place_picture
SET status = 'inactive'
WHERE place_id = #{place_id}
