mapper.select:
SELECT p.place_id, p.place_nm, pp.picture_source FROM place
p, place_picture
pp where p.place_id = pp.place_id ;

SELECT p.place_id, p.place_nm, pp.picture_source
FROM place p
JOIN place_picture pp ON p.place_id = pp.place_id
JOIN room r ON p.place_id = r.place_id
WHERE p.loc_id = #{location}
AND #{guests} BETWEEN r.min_people AND r.max_people
 AND NOT EXISTS (
    SELECT 1
    FROM unavailable_date ud
    WHERE ud.room_id = r.room_id
    AND DATE_FORMAT(ud.reserved_date, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
);
